import React, { useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
// import Validation from "./SignupValidation";

function Signup() {
  //   const [errors, setErrors] = useState({});
  const [values, setValues] = useState({
    name: "",
    username: "",
    password: "",
  });

  const handleInput = (event) => {
    setValues((prev) => ({
      ...prev,
      [event.target.name]: event.target.value,
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    // setErrors(Validation(values));

    const res = await axios.post("http://localhost:3001/signup", values);
    console.log(res);

    const createdUsername = res.data.username;
    console.log("Created username:", createdUsername);

    // if (
    //   errors.name === "" &&
    //   errors.username === "" &&
    //   errors.password === ""
    // ) {
    //   try {
    //     const res = await axios.post("http://localhost:3001/signup", values);
    //     console.log(res);

    //     const createdUsername = res.data.username;
    //     console.log("Created username:", createdUsername);
    //   } catch (err) {
    //     console.log(err);
    //   }
    // }
  };

  return (
    <div className="d-flex justify-content-center align-items-center bg-primary vh-100">
      <div className="bg-white p-3 rounded-w-25">
        <h2 className="fs-1 mb-3">Sign-Up</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="name">
              <strong>Name:</strong>
            </label>
            <input
              id="name"
              name="name"
              type="text"
              placeholder="Enter Name"
              onChange={handleInput}
              className="form-control rounded-0"
            />
            {/* {errors.name && <span className="text-danger">{errors.name}</span>} */}
          </div>
          <div className="mb-3">
            <label htmlFor="email">
              <strong>Username:</strong>
            </label>
            <input
              type="email"
              name="username"
              placeholder="Enter Username"
              onChange={handleInput}
              className="form-control rounded-0"
            />
            {/* {errors.username && (
              <span className="text-danger">{errors.username}</span>
            )} */}
          </div>

          <div className="mb-3">
            <label htmlFor="password">
              <strong>Password:</strong>
            </label>
            <input
              type="password"
              name="password"
              placeholder="Enter Password"
              onChange={handleInput}
              className="form-control rounded-0"
            />
            {/* {errors.password && (
              <span className="text-danger">{errors.password}</span>
            )} */}
          </div>

          <button type="submit" className="btn btn-success w-100 rounded-0">
            Sign up
          </button>
          <p className="mb-3">You are agree to our terms and policies</p>
          <Link
            to="/login"
            className="btn btn-default border w-100 bg-light rounded-0 text-decoration-none"
          >
            Log in
          </Link>
        </form>
      </div>
    </div>
  );
}

export default Signup;
